def load_sentences(path, lower, zeros):
    """
    Load sentences. A line must contain at least a word and its tag.
    Sentences are separated by empty lines.
    """
    #参数：path  'e:\\objectTest\\NERuselocal\\data\\example.train'训练(已经标志)文件路径   lower  字母转换小写， zeros数字转换0
    #加载句子。一行必须至少包含一个单词和它的标签。句子用空行分隔
    sentences = []#存放整个数据集。多个句子的组合
    sentence = []#存放的是一个句子
    num = 0
    for line in open(path, 'r',encoding='utf8'): #path 'e:\\objectTest\\NERuselocal\\data\\example.train'
        num+=1  #删除 string 字符串末尾的指定字符（默认为空格）
        line = zero_digits(line.rstrip()) if zeros else line.rstrip() #如果是数字去掉尾部空格转换为0 否则去掉尾部空格
        #1次循环 '无 O'  '长 B-Dur' '期 I-Dur' 
        # print(list(line))
        if not line:#如果空行，句子结束 加入到sentences  sentence出事化为空
            if len(sentence) > 0:
                sentences.append(sentence)
                sentence = []
        else:
            if line[0] == " ": #如果是空字符  用"$"代替
                line = "$" + line[1:]
                word = line.split()
                # word[0] = " "  读取每行字，对应的标记存入list ['无', 'O'] 然后加入到 sentence
            else:
                word= line.split( )#['无', 'O'] ['长', 'B-Dur'] ['期', 'I-Dur']
            assert len(word) == 2
            sentence.append(word) #[['无', 'O'], ['长', 'B-Dur'],['期', 'I-Dur']]
    if len(sentence) > 0:
        #if 'DOCSTART' not in sentence[0][0]:
            sentences.append(sentence)
    return  sentences


def update_tag_scheme(sentences, tag_scheme):
    """
    Check and update sentences tagging scheme to IOB2.
    Only IOB1 and IOB2 schemes are accepted.
    """
    #检查并更新IOB2的句子标注方案。仅接受IOB1和IOB2方案  其中最常用的是IOB2、IOBS、IOBES。
    for i, s in enumerate(sentences):#s一个句子
        tags = [w[-1] for w in s] #w[-1]每个字的标注
        # Check that tags are given in the IOB format
        if not iob2(tags):#iob2(tags) 函数对每个字的标注做判断(符合 IOB1 to IOB2)是否一个合法的标注
            s_str = '\n'.join(' '.join(w) for w in s)
            raise Exception('Sentences should be given in IOB format! ' +
                            'Please check sentence %i:\n%s' % (i, s_str))
        if tag_scheme == 'iob':
            # If format was IOB1, we convert to IOB2
            for word, new_tag in zip(s, tags):
                word[-1] = new_tag
        elif tag_scheme == 'iobes':
            new_tags = iob_iobes(tags)  #iob 转换成 iobes
            for word, new_tag in zip(s, new_tags):#new_tags 转换后的编码
                word[-1] = new_tag
        else:
            raise Exception('Unknown tagging scheme!')


def char_mapping(sentences, lower):
    """
    Create a dictionary and a mapping of words, sorted by frequency.
    """
    #创建一个字典映射，按词频排序 s每句话，x是['无', 'O'] 把字转换成小写
    chars = [[x[0].lower() if lower else x[0] for x in s] for s in sentences] #[['以', '冠', '心', '病', '收', '住', '入', '院', '。'],  ['音', '。']]
    dico = create_dico(chars)#统计字频
    dico["<PAD>"] = 10000001  #pading 每个字加个边框 每句话的长度pading 成一样的长度 做卷积
    dico['<UNK>'] = 10000000  #不知道的字也给个id
    char_to_id, id_to_char = create_mapping(dico) #每个字建立以个字典
    #字到id  char_to_id   id到字 id_to_char
    #print("Found %i unique words (%i in total)" % (
    #    len(dico), sum(len(x) for x in chars)
    #))
    # dico 是没有排序的字典{'无': 79775, '长': 3134, '期': 7713, '0': 335904, '年': 19664, '月': 14307}
    # char_to_id  {0: '<PAD>', 1: '<UNK>', 2: '0', 3: '，', 4: '：', 5: '。', 6: '无',}  字频从大到小
    # id_to_char  {'<PAD>': 0, '<UNK>': 1, '0': 2, '，': 3, '：': 4, '。': 5, '无': 6,}  字频从大到小
    return dico, char_to_id, id_to_char


def tag_mapping(sentences):
    """
    Create a dictionary and a mapping of tags, sorted by frequency.
    """
    #创建一个字典(标签的映射).按词频排序
    f=open('tag_to_id.txt','w',encoding='utf8')
    f1=open('id_to_tag.txt','w',encoding='utf8')
    tags=[]
    for s in sentences:
        ts=[]
        for char in s:
            tag=char[-1]
            ts.append(tag)
        tags.append(ts)
    
    #tags1 = [[char[-1] for char in s] for s in sentences]
    dico = create_dico(tags)#字频
    tag_to_id, id_to_tag = create_mapping(dico)  #排序
    #print("Found %i unique named entity tags" % len(dico))
    for k,v in tag_to_id.items():
        f.write(k+":"+str(v)+"\n")
    for k,v in id_to_tag.items():
        f1.write(str(k) + ":" + str(v) + "\n")
    return dico, tag_to_id, id_to_tag


