def augment_with_pretrained(dictionary, ext_emb_path, chars):
    """
    Augment the dictionary with words that have a pretrained embedding.
    If `words` is None, we add every word that has a pretrained embedding
    to the dictionary, otherwise, we only add the words that are given by
    `words` (typically the words in the development and test sets.)
    """
    #print('Loading pretrained embeddings from %s...' % ext_emb_path)
    assert os.path.isfile(ext_emb_path)#检测是否字向量文件 'e:\\objectTest\\NERuselocal\\data\\vec.txt'

    # Load pretrained embeddings from file
    pretrained = set([ #set去掉重复的字。把字提取出来
        line.rstrip().split()[0].strip()  #每行去换行符，空格隔开
        for line in codecs.open(ext_emb_path, 'r', 'utf-8')#codecs打开文件的一个库
        if len(ext_emb_path) > 0
    ])

    # We either add every word in the pretrained file,
    # or only words given in the `words` list to which
    # we can assign a pretrained embedding
    if chars is None:# chars 是训练集中拉平的字 看看在 文件字典库中有没有这个字 
        for char in pretrained:  
            if char not in dictionary:  
                dictionary[char] = 0  #如果 训练集中的字   不在文件库中  也不在 训练的字典 中 就添加到训练字典中
    else:
        for char in chars:  #每个字 any 判断x 是三种中的任意一种返回Ture 有个不为空就返回false 
            if any(x in pretrained for x in [
                char,
                char.lower(),#转小写
                re.sub('\d', '0', char.lower())#数字替换为0
            ]) and char not in dictionary:
                dictionary[char] = 0     #如果字在 原始文件pretrained中 并且不在训练字典中（可能是数字转，字母，）  添加到训练字典中

    word_to_id, id_to_word = create_mapping(dictionary)  #对新生产的训练字典 排序重新返回
    return dictionary, word_to_id, id_to_word


